pluginOptions.query:
{
  site {
    siteMetadata {
      rssMetadata {
        site_url
        feed_url
        title
        description
        image_url
        author
        copyright
      }
    }
  }
}

feeds.query:
{
  allMarkdownRemark(
    limit: 1000,
    sort: { order: DESC, fields: [frontmatter___date] },
  ) {
    edges {
      node {
        excerpt
        html
        timeToRead
        fields { slug }
        frontmatter {
          title
          cover
          date
          category
          tags
        }
      }
    }
  }
}
